import React, { Component } from 'react';
import './App.css';
import { PersonaService } from './service/PersonaService';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import {Panel} from 'primereact/panel';

export default class App extends Component{
  constructor(){
    super();
    this.state = {
      visible : false,
      persona: {
        nombre: null,
        apellido: null
      },
      selectedPersona : {

      }
    };
    this.personaService = new PersonaService();

  }

  componentDidMount(){
    this.personaService.getAll().then(data => this.setState({personas: data}))
  }


  render(){
    return (
<Panel header="React CRUD App">
            <DataTable value={this.state.personas} paginator={true} rows="2" selectionMode="single" selection={this.state.selectedPersona} onSelectionChange={e => this.setState({selectedPersona: e.value})}>
              <Column field="nombre" header="Nombre"></Column>
              <Column field="apellido" header="Apellido"></Column>
            </DataTable>
        </Panel>
    )
  }
}
